function open(url, target, isExt) {
    var ref = null,
        that = this;

    function _getRelativePath() {
      var result = 'index.html';

      switch (that.require('state').menu()) {
        case 'systems':
          if (that.require('factory').system()) {
            result = result + '#' + that.require('factory').system().id() + '#systems#' + that.require('factory').system().name();
          }
          break;
        case 'schemas':
          if (that.require('factory').system()) {
            result = result + '#' + that.require('factory').system().id() + '#schemas';
            if (that.require('state').space()) {
             result = result + '#' + that.require('state').space();
            }
          }
          break;
        case 'models':
          if (that.require('factory').system()) {
            result = result + '#' + that.require('factory').system().id() + '#models';
            if (that.require('state').space()) {
             result = result + '#' + that.require('state').space();
            }
          }
          break;
        case 'types':
          if (that.require('factory').system()) {
            result = result + '#' + that.require('factory').system().id() + '#types';
            if (that.require('state').space()) {
             result = result + '#' + that.require('state').space();
            }
          }
          break;
        case 'behaviors':
          if (that.require('factory').system()) {
            result = result + '#' + that.require('factory').system().id() + '#behaviors';
            if (that.require('state').space()) {
             result = result + '#' + that.require('state').space();
            }
          }
          break;
        case 'components':
          if (that.require('factory').system()) {
            result = result + '#' + that.require('factory').system().id() + '#components';
            if (that.require('state').space()) {
             result = result + '#' + that.require('state').space();
            }
          }
          break;
        case 'logs':
          if (that.require('factory').system()) {
            result = result + '#' + that.require('factory').system().id() + '#logs';
          }
          break;
        default:
          break;
      }

      return result;
    }

    switch(true) {
      case this.isElectron():
        if (isExt) {
            ref = require('electron').shell.openExternal(url, target);  
        } else {
            if (target === '_self') {
              window.location.href = url;
            } else {
              ref = window.open(url, target);
              ref.focus();
            }
        }
        break;
      case this.isCordova():
        if (isExt) {
          window.open(url, '_system');
        } else { 
          if (url.indexOf('index.html') === -1 || url.indexOf('app/') !== -1) {
            /*
            var system = this.require('db').collections().System.find({
                '_id': this.require('factory').system().id()
            })[0];
            system = JSON.parse(JSON.stringify(system));
            delete system.classInfo;

            url = url + '?system=' + encodeURIComponent(JSON.stringify(system));
            url = url + '&ref=' + encodeURIComponent(_getRelativePath());
            */
            url = url + '?ref=' + encodeURIComponent(_getRelativePath());
          }
          document.location.href = url;
        }
        break;
      default:
        ref = window.open(url, target);
        break;
    }

    return ref;
}

