function setEditor(mode, value, lineNumber, autocomplete) {
  var language = {
    'text': 'text/x-textile',
    'json': 'application/json',
    'javascript': 'text/javascript',
    'html': 'htmlmixed',
    'css': 'text/css'
  };

  switch (this.type()) {
    case 'ace':
      switch (this.context()) {
        case 'system':
        case 'schema':
        case 'model':
        case 'type':
          this.editor().getSession().setMode('ace/mode/' + mode);
          this.editor().setValue(value);
          this.editor().gotoLine(lineNumber);
          this.editor().getSession().$undoManager.reset();
          this.editor().getSession().setUndoManager(new ace.UndoManager());
          this.editor().getSession().setTabSize(2);
          this.editor().focus();
          break;
        case 'behavior':
          if (mode === 'javascript') {
            this.editor().getSession().setMode('ace/mode/' + mode);
            this.editor().getSession().setTabSize(2);

            var completer = {
              getCompletions: function (editor, session, pos, prefix, callback) {
                var systemId = '',
                  result = [],
                  behavior = {},
                  schemaName = '',
                  schemas = {},
                  schema = {},
                  parents = {},
                  i = 0;

                if (this.require('state').menu() !== 'action') {
                  return;
                }

                function hasDot() {
                  var result = false;
                  if (editor.session.getLine(pos.row)[pos.column - 1 - prefix.length] === '.') {
                    result = true;
                  }
                  return result;
                }

                function _getSchema(schemas, name) {
                  var result = '',
                    id = '';

                  for (id in schemas) {
                    if (schemas[id]._name === name) {
                      result = schemas[id];
                      break;
                    }
                  }
                  return result;
                }

                function _findSchemaName(id, components) {
                  var result = id,
                      modelName = '';

                  for (modelName in components) {
                    if (typeof components[modelName][id] !== 'undefined') {
                      result = modelName;
                      break;
                    }
                  }
                  return result;
                }  

                id = document.location.href.split('#')[1];
                systemId = document.location.href.split('#')[2];

                system = this.require('storage').get(systemId);
                if (system && hasDot()) {
                  schemaName = _findSchemaName(system.behaviors[id].component, this.require('factory').system().components());
                  schemas = system.schemas;

                  schema = _getSchema(schemas, schemaName);

                  for (var name in schema) {
                    if (name.indexOf('_') !== 0) {
                      result.push({ name: name, caption: name, snippet: name + '($0)', meta: schema[name] });
                    }
                  }

                  // case of system
                  if (system.behaviors[id].component === systemId) {
                    if ('id'.indexOf(prefix) === 0) {
                      result.push({ name: 'id', caption:'id', snippet: 'id()', meta: 'property (inherited)' });
                    }
                    if ('classInfo'.indexOf(prefix) === 0) {
                      result.push({ name: 'classInfo', caption:'classInfo', snippet: 'classInfo()', meta: 'method (inherited)' });
                    }
                    if ('on'.indexOf(prefix) === 0) {
                      result.push({ name: 'on', caption:'on', snippet: 'on($0)', meta: 'method (inherited)' });
                    }
                    if ('off'.indexOf(prefix) === 0) {
                      result.push({ name: 'off', caption:'off', snippet: 'off($0)', meta: 'method (inherited)' });
                    }
                    if ('require'.indexOf(prefix) === 0) {
                      result.push({ name: 'require', caption:'require', snippet: 'require(\'$0\')', meta: 'method (inherited)' });
                    }
                    if ('destroy'.indexOf(prefix) === 0) {
                      result.push({ name: 'destroy', caption:'destroy', snippet: 'destroy()', meta: 'method (inherited)' });
                    }
                    if ('init'.indexOf(prefix) === 0) {
                      result.push({ name: 'init', caption:'init', snippet: 'init($0)', meta: 'method (inherited)' });
                    }
                    if ('error'.indexOf(prefix) === 0) {
                      result.push({ name: 'error', caption:'error', snippet: 'error($0)', meta: 'event (inherited)' });
                    }
                  }

                  // inherited
                  parents = schema._inherit;
                  if (parents) {
                    length = parents.length;

                    for (i = 0; i < length; i++) {
                      if (parents[i].indexOf('_Component') !== -1) {
                        if ('id'.indexOf(prefix) === 0) {
                          result.push({ name: 'id', caption:'id', snippet: 'id()', meta: 'property (inherited)' });
                        }
                        if ('classInfo'.indexOf(prefix) === 0) {
                          result.push({ name: 'classInfo', caption:'classInfo', snippet: 'classInfo()', meta: 'method (inherited)' });
                        }
                        if ('on'.indexOf(prefix) === 0) {
                          result.push({ name: 'on', caption:'on', snippet: 'on($0)', meta: 'method (inherited)' });
                        }
                        if ('off'.indexOf(prefix) === 0) {
                          result.push({ name: 'off', caption:'off', snippet: 'off($0)', meta: 'method (inherited)' });
                        }
                        if ('require'.indexOf(prefix) === 0) {
                          result.push({ name: 'require', caption:'require', snippet: 'require(\'$0\')', meta: 'method (inherited)' });
                        }
                        if ('destroy'.indexOf(prefix) === 0) {
                          result.push({ name: 'destroy', caption:'destroy', snippet: 'destroy()', meta: 'method (inherited)' });
                        }
                        if ('init'.indexOf(prefix) === 0) {
                          result.push({ name: 'init', caption:'init', snippet: 'init($0)', meta: 'method (inherited)' });
                        }
                        if ('error'.indexOf(prefix) === 0) {
                          result.push({ name: 'error', caption:'error', snippet: 'error($0)', meta: 'event (inherited)' });
                        }
                      } else {
                        schema = _getSchema(schemas, parents[i]);

                        for (var prop in schema) {
                          if (prop.indexOf('_') !== 0 && prop.indexOf(prefix) === 0) {
                            result.push({ name: prop, caption: prop, snippet: prop + '($0)', meta: schema[prop] + ' (inherited)' });
                          }
                        }
                      }
                    }
                  }
                }

                // snippets
                if (hasDot()) {
                  if ('collections'.indexOf(prefix) === 0) {
                    result.push({ name: 'snip_collections', caption: 'collections', snippet:  'this.require(\'db\').collections()', meta: 'snippet' });
                  }
                  if ('require'.indexOf(prefix) === 0) {
                    result.push({ name: 'snip_require', caption: 'require', snippet:  'this.require(\'$0\')', meta: 'snippet' });
                  }
                  if ('debug'.indexOf(prefix) === 0) {
                    result.push({ name: 'snip_debug', caption: 'debug', snippet:  'this.require(\'logger\').debug(\'$0\')', meta: 'snippet' });
                  }
                  if ('info'.indexOf(prefix) === 0) {
                    result.push({ name: 'snip_info', caption: 'info', snippet:  'this.require(\'logger\').info(\'$0\')', meta: 'snippet' });
                  }
                  if ('warn'.indexOf(prefix) === 0) {
                    result.push({ name: 'snip_warn', caption: 'warn', snippet:  'this.require(\'logger\').warn(\'$0\')', meta: 'snippet' });
                  }
                  if ('error'.indexOf(prefix) === 0) {
                    result.push({ name: 'snip_error', caption: 'error', snippet:  'this.require(\'logger\').error(\'$0\')', meta: 'snippet' });
                  }
                }

                result.sort(function (a, b) {
                  if (a.name > b.name) {
                    return 1;
                  }
                  if (a.name < b.name) {
                    return -1;
                  }
                  return 0;
                }
                );
                callback(null, result);
              }.bind(this)
            };

            this.editor().setOptions({
              enableLiveAutocompletion: [completer]
            });
            this.editor().setValue(value);

            this.editor().gotoLine(lineNumber);

            this.editor().getSession().$undoManager.reset();
            this.editor().getSession().setUndoManager(new ace.UndoManager());
            this.editor().focus();
          } else {
            this.editor().getSession().setMode('ace/mode/' + mode);
            this.editor().setValue(value);
            this.editor().gotoLine(lineNumber);
            this.editor().getSession().$undoManager.reset();
            this.editor().getSession().setUndoManager(new ace.UndoManager());
            this.editor().focus();
          }
          break;
        case 'component':
          this.editor().getSession().setMode('ace/mode/' + mode);
          this.editor().getSession().setTabSize(2);
          if (autocomplete) {
            this.editor().setOptions({
              enableLiveAutocompletion: true,
            });
          }
          this.editor().setValue(value);
          this.editor().gotoLine(lineNumber);
          this.editor().getSession().$undoManager.reset();
          this.editor().getSession().setUndoManager(new ace.UndoManager());
          this.editor().focus();
          break;
        default:
          break;
      }
      break;
    case 'codemirror':
      switch (this.context()) {
        case 'system':
        case 'schema':
        case 'model':
        case 'type':
        case 'behavior':
        case 'component':
          this.editor().setValue('');
          this.editor().setOption('mode', language[mode]);
          this.editor().setValue(value);
          this.editor().setCursor({
            line: lineNumber - 1,
            ch: 0
          });
          this.editor().clearHistory();
          break;
        default:
          break;
      }
      break;
    default:
      break;
  }
}

