function start() {
  var bundle = '';
  var id = '';
  var system = '';
  var logger = this.require('logger');
  var helper = this.require('runtimeHelper');
  var storeConfig = this.require('storage').get('system-designer-config');

  // check platform
  helper.checkElectron();
  helper.checkCordova();

  // get the bundle
  bundle = helper.getBundle();

  logger.level('debug');

  // import the bundle
  logger.debug('analyzing ' + bundle.name +' system...');
  id = $db.importSystem(bundle);

  // update title
  document.title = bundle.name;

  // start the bundle
  system = this.require(id);

  if (system && system.start) {
    setTimeout(function() { 
      logger.debug('starting ' + bundle.name + ' system...');
      system.start();
    }, 100);
  } else {
    logger.debug('starting ' + bundle.name + ' system...');
  }

  setTimeout(function() {
    let domNb = 5;

    if (typeof cordova !== 'undefined') { domNb = 9 }
    if (navigator.userAgent.toLowerCase().indexOf('electron') !== -1) { domNb = 4 }

    if (document.body.children.length === domNb) {
      var message = document.getElementById('message');
      if (message) message.style.display = 'flex'; 
    }}, 100);
}

