function checkCordova() {
  var params = '';

  function _getParams() {
    var result = {};
    var urlParams = [];
    var system = null;

    urlParams = document.location.href.split('?');

    if (urlParams.length > 1) {
      urlParams = urlParams[1].split('&');
      urlParams.forEach(function (urlParam) {
        var name = '',
          value = '';

        name = urlParam.split('=')[0].trim();
        value = urlParam.split('=')[1].trim();

        result[name] = decodeURIComponent(value);
      });
    }
    return result;
  }
  // case of cordova    
  if (typeof cordova !== 'undefined') {

    window.messages = [];
    window.lastPage = 'index.html';

    params = _getParams();

    if (Object.keys(params).length) {
      lastPage = params.ref;
    }

