function getBundle() { 
  var storage = this.require('storage');
  var sysId = '';
  var bundle = '';

  sysId = document.location.href.split('#')[1].split('?')[0].split('/')[0];
  bundle = storage.get(sysId);

  // listen to messages from System Designer
  storage.on('changed', function (obj) {
    if (typeof obj['system-designer-message'] !== 'undefined') {
      $db._Message.insert(obj['system-designer-message'].newValue);
    }
  }, true, true);

  return bundle;
}
