function render(dotFile, diagramType) { 
  var dotGraph = '',
      storeConfig = null;

  switch(true) {
    case dotFile !== '':
      dotGraph = dotFile;
      document.title = 'diagrams';
      break;
    case this.data() !== '':
      dotGraph = this.data();
      document.title = 'diagrams';
      break;
    case this.require('factory').system() !== undefined:
      dotGraph = this.require('export').toGraphviz(diagramType);
      break;
    default:  
      document.title = 'diagrams';
      $('#designer-diagram').empty();
      this.require('message-help').render();
      return;
  }

  if (!this.ref()) {
    this.ref(new Viz());
  }

  this.ref().renderSVGElement(dotGraph)
  .then(function(element) {
    var elt = document.getElementById('designer-diagram');

    $('#designer-diagram').empty();
    elt.appendChild(element);

    var svg = document.getElementsByTagName('svg')[0];

    // set width and heigth
    svg.setAttribute('width', ($(document).width() - 50) + 'px');
    svg.setAttribute('height', ($(document).height() - 50) + 'px');

     var panZoom = svgPanZoom(svg, {
          zoomEnabled: true,
          controlIconsEnabled: false,
          fit: true,
          center: true,
          minZoom: 0.1
        });

    this.refZoom(panZoom);

    svg.addEventListener('paneresize', function(e) {
      this.refZoom().resize();
    }.bind(this), false);

    window.addEventListener('resize', function(e) {
      this.refZoom().resize();
    }.bind(this));

  }.bind(this))
  .catch(error => {
    if (!runtime.require('viz').ref()) {
      runtime.require('viz').ref(new Viz());
    }

    runtime.require('message').danger(error.toString());
  });
}

