function init(conf) { 
  var that = this;

  $('html')
    .on('dragenter dragover', false)
    .on('drop', function (e) {
      e.stopPropagation();
      e.preventDefault();

      var files = e.originalEvent.dataTransfer.files;
      var reader = new FileReader();
      var dotFile = '';
      reader.onload = function (event) {
        dotFile += event.target.result;
      };
      reader.onloadend = function () {
        var DialogDropDiagram = that.require('DialogDropDiagram');

        DialogDropDiagram = new DialogDropDiagram({
          'title': 'A diagram has been found',
          'message': 'You can view the diagram.'
        });

        DialogDropDiagram.data(dotFile);
        DialogDropDiagram.show();
      };
      if (files[0] && files[0].name && files[0].name.indexOf('.gv') !== -1) {
        reader.readAsText(files[0], 'UTF-8');
      }
    });	
}

