function render() {
  var systemId = '',
    system = null,
    sys = null,
    id = '',
    behavior = null;

  // type
  this.require('state').type(window.location.href.split('.html')[0].split('/')[window.location.href.split('.html')[0].split('/').length - 1]);

  if (this.isCordova()) {
    this.updateCordovaContext();
  }

  // set system
  id = document.location.href.split('#')[1].split('?')[0];
  systemId = document.location.href.split('#')[2].split('?')[0];

  system = this.require('storage').get(systemId);

  sys = this.require('factory').create(system);

  // extensions
  this.installExtensions();

  // render
  this.require('menubar').render();
  this.require('toolbar').render();
  this.require('workspace').render();

  // set store
  behavior = this.require('storage').get(systemId).behaviors[id];

  this.require('store').uuid(id);
  this.require('store').data(behavior);

  if (behavior.state === 'start' && systemId === behavior.component) {
    document.title = 'behavior ' + this.require('factory').system().name() + '.' + behavior.state + ' · system ' + this.require('storage').get(systemId).name;
  } else {
    document.title = 'behavior ' + behavior.component + '.' + behavior.state + ' · system ' + this.require('storage').get(systemId).name;
  }

  this.require('editor').initValue(behavior.action, 2);

  this.require('router').update();

  this.addShortcuts();
}

