function save() { 
  var val = this.require('editor').getValue(),
    designer = this.require('designer'),
    store = this.require('store').data(),
    systems = this.require('storage').get('system-designer-systems'),
    message = this.require('message');

  if (systems && systems.systems.length === 0) {
    message.danger('Can not save your behavior: there is no system.');
    return;
  }

  if (this.require('state').menu() === 'action') {
    store.action = val;
  } else {
    store = JSON.parse(val);
  }
  this.require('store').data(store);

  // check if ID change
  if (this.require('store').uuid() !== this.require('store').data()._id) {
    this.require('channel').$editorDeleteBehavior(this.require('store').uuid());
    this.require('store').uuid(this.require('store').data()._id);
  }

  if (store.state === 'start' && this.require('factory').system().id() === store.component) {
    document.title = 'behavior ' + this.require('factory').system().name()+ '.start ' + ' · ' + document.title.split('·')[1].trim();
  } else {
    document.title = 'behavior ' + this.require('store').data().component + '.' + this.require('store').data().state + ' · ' + document.title.split('·')[1].trim();
  }

  this.require('channel').$editorUpdateBehavior(this.require('store').uuid(), this.require('store').data());
  message.clean();
  message.success('Behavior saved.');
}

