        this.require('store').data()[oldContext] = JSON.parse(editor.getValue());
      } else {
        this.require('store').data()[oldContext] = editor.getValue();
      }
    } else {
      this.require('store').data(JSON.parse(editor.getValue()));
    }
  }

  component = this.require('store').data();

