function save() { 
  var val = this.require('editor').getValue(),
    designer = this.require('designer'),
    message = this.require('message'),
    store = this.require('store').data(),
    state = this.require('state'),
    extra = this.require('store').extra(),
    factory = this.require('factory'),
    id = '',
    systems = this.require('storage').get('system-designer-systems');

  if (systems && systems.systems.length === 0) {
    message.danger('Can not save your component: there is no system.');
    return;
  }

  if (state.menu() === 'runtimeComponent') {
    try {
      store = JSON.parse(val);
    } catch (e) {
      message.danger('Can not save your component: your component has an invalid structure.');
      return;
    }

    if (!store._id) {
      id = decodeURIComponent(document.location.href.split('#')[1].split('?')[0]);
      if (id) {
        store._id = id;
      } else {
        store._id = factory.generateId();
      }
    }
    if (store._id && store._id.indexOf(' ') !== -1) {
      message.danger('Invalid \'_id\'. <br>Space is not authorized in the value of \'_id\'.');
      return;
    }
  } else {
    if (extra[state.menu()] === 'json') {
      store[state.menu()] = JSON.parse(val);
    } else {
      store[state.menu()] = val;
    }
  }
  this.require('store').data(store);

  // check if ID change
  if (this.require('store').uuid() !== this.require('store').data()._id) {
    this.require('channel').$editorUpdateComponentId(this.require('store').uuid(), this.require('store').data()._id, this.require('store').collection());
    this.require('channel').$editorDeleteComponent(this.require('store').uuid(), this.require('store').collection());

    this.require('store').uuid(this.require('store').data()._id);

    // update title
    document.title = 'component ' + this.require('store').uuid() + ' · ' + document.title.split('·')[1].trim();
  }

  this.require('channel').$editorUpdateComponent(this.require('store').uuid(), this.require('store').collection(), this.require('store').data());
  this.require('message').clean();
  this.require('message').success('Component saved.');
}

