function start() { 
  var storeConfig = '';

  // set locale
  storeConfig = this.require('storage').get('system-designer-config');

  if (!storeConfig) {
    storeConfig = {};
  }

  if (typeof storeConfig.language === 'undefined') {
    storeConfig.language = 'en-US';
    this.require('storage').set('system-designer-config', storeConfig);
  } else {
    this.require('i18n').locale(storeConfig.language);
  }

  this.require('designer').render();
}

