function render() {
  var id = '',
    systemId = '',
    system = null,
    schema = null;

  // type
  this.require('state').type(window.location.href.split('.html')[0].split('/')[window.location.href.split('.html')[0].split('/').length - 1]);

  if (this.isCordova()) {
    this.updateCordovaContext();
  }

  // render
  this.require('menubar').render();
  this.require('toolbar').render();
  this.require('workspace').render();

  // get parameters
  id = document.location.href.split('#')[1].split('?')[0];
  systemId = document.location.href.split('#')[2].split('?')[0];

  // set editor
  system = this.require('storage').get(systemId);

  this.require('factory').create(system);

  // extensions
  this.installExtensions();

  schema = system.schemas[id];

  this.require('store').uuid(id);
  this.require('store').data(schema);

  document.title = 'schema ' + schema._name + ' · system ' + system.name;

  this.require('editor').initValue(JSON.stringify(schema, null, '\t'), 2);

  // router
  this.require('router').update();

  // shorcuts
  this.addShortcuts();
}

