function save() { 
  var val = this.require('editor').getValue(),
    designer = this.require('designer'),
    message = this.require('message'),
    schema = null,
    property = '',
    propVal = '',
    hasError = false,
    systems = this.require('storage').get('system-designer-systems');

  if (systems && systems.systems.length === 0) {
    message.danger('Can not save your schema: there is no system.');
    return;
  }

  try {
    schema = JSON.parse(val);
  } catch (e) {
    message.danger('Can not save your schema: your schema has an invalid structure.');
    return;
  }

  // global properties check
  for (property in schema) {
    if (schema.hasOwnProperty(property) && property.indexOf('_') !== 0) {
      propVal = schema[property];
      if (['property', 'link', 'collection', 'event', 'method'].indexOf(propVal) === -1) {
        message.danger('Invalid value for the property \'' + property + '\’.<br>Authorized values are: \'property\', \'link\', \'collection\', \'event\' and \'method\'.');
        return;
      }
    }
  }

  for (property in schema) {
    if (schema.hasOwnProperty(property) && property.indexOf('_') === 0) {
      if (['_id', '_name', '_inherit', '_class', '_core', '_description'].indexOf(property) === -1) {
        message.danger('Invalid value \'' + property + '\’.<br>Authorized values are: \'_id\', \'_name\', \'_inherit\', \'_class\', \'_core\' and \'_description\'.');
        return;
      }
    }
  }

  for (property in schema) {
    if (property.indexOf(' ') !== -1) {
      message.danger('Invalid property name \'' + property + '\’. <br>Space is not authorized in the name of a property.');
      return;
    }
    if (property.match(/[\-\!\"\#\$\%\&\'\(\)\*\+\,\.\/\:\;\<\=\>\?\@\[\\\]\^\`\{\|\}\~]/gi)) {
      message.danger('Invalid property name \'' + property + '\’. <br>Invalid character found in the name of a property.');
      return;
    }
    propVal = schema[property];
    if (['property', 'link', 'collection', 'event', 'method'].indexOf(propVal) !== -1 && property.indexOf('_') === 0) {
      message.danger('Invalid property name \'' + property + '\’. <br>A property name can not start with \'_\'.');
      return;
    }
  }

  // _inherit check
  if (typeof schema._inherit !== 'undefined' && !Array.isArray(schema._inherit)) {
    message.danger('Invalid inheritance value: it must be an array.');
    return;
  }

  if (schema._inherit && schema._inherit.indexOf(schema._name) !== -1) {
    message.danger('Invalid inheritance value: you can not inherit from yourself.');
    return;
  }

  if (schema._inherit && schema._inherit.indexOf('') !== -1) {
    message.danger('Invalid inheritance value: it can not be empty.');
    return;
  }

  if (schema._inherit) {
    schema._inherit.forEach(function (parent) {
      if (parent.constructor.name !== 'String') {
        hasError = true;
      }
    });
    if (hasError) {
      message.danger('Invalid inheritance value: it must be a string.');
      return;
    }
  }

  // _name check
  if (typeof schema._name === 'undefined') {
    message.danger('The property \'_name\' is missing.');
    return;
  }

   if (schema._name.constructor.name !== 'String') {
    message.danger('The property \'_name\' must be a string.');
    return;
  }

  // _id check
  if (typeof schema._id === 'undefined') {
    message.danger('The property \'_id\' is missing.');
    return;
  }

  if (schema._id.constructor.name !== 'String') {
    message.danger('The property \'_id\' must be a string.');
    return;
  }

  // clean name
  schema._name = schema._name.replace(/\s|\t/gi, '_')
    .replace(/[\-\!\"\#\$\%\&\'\(\)\*\+\,\.\/\:\;\<\=\>\?\@\[\\\]\^\`\{\|\}\~]/gi, '')
    .replace(/\r|\n/gi,'');

  // check if _name had changed
  if (this.require('store').data()._name !== schema._name) {
    this.require('channel').$editorUpdateSchemaName(schema._name, this.require('store').uuid());
    document.title = 'schema ' + schema._name + ' · ' + document.title.split('·')[1].trim();
  }

  this.require('store').data(schema);

  // check if _id has changed
  if (this.require('store').uuid() !== this.require('store').data()._id) {
    this.require('channel').$editorUpdateSchemaId(this.require('store').uuid(), this.require('store').data()._id);
    this.require('store').uuid(this.require('store').data()._id);
  }

  this.require('channel').$editorUpdateSchema(this.require('store').uuid(), this.require('store').data());
  message.success('Schema saved.');
}

