function save() { 
  var val = this.require('editor').getValue(),
    designer = this.require('designer'),
    store = this.require('store').data(),
    systems = this.require('storage').get('system-designer-systems');

  if (systems && systems.systems.length === 0) {
    this.require('message').danger('Can not save: there is no system.');
    return;
  }

  switch (this.require('state').menu()) {
    case 'name':
      val = val.trim();
      val = val.replace(/\s|\t/gi, '-')
      .replace(/[\!\"\#\$\%\&\'\(\)\*\+\,\.\/\:\;\<\=\>\?\@\[\\\]\^\`\{\|\}\~]/gi, '')
      .replace(/\r|\n/gi,'');
      this.require('editor').setValue(val);
      store.name = val;
      document.title = 'system ' + store.name;
      break;
    case 'description':
      store.description = val.trim();
      break;
    case 'version':
      store.version = val.trim();
      break;
    case 'bundle':
      store = JSON.parse(val);
      document.title = 'system ' + store.name;
      break;
    default:
      break;
  }

  this.require('store').data(store);

  this.require('channel').$editorUpdateSystem(this.require('store').uuid(), this.require('store').data());
  this.require('message').clean();
  this.require('message').success('System saved.');
}

