function addShorcuts() { 
  if (!this.isCordova()) {
  	if (this.isMac()) {
      document.body.addEventListener('keydown', function keydown(event){
        var message = this.require('message'),
            key = event.which || event.keyCode,
            command = event.metaKey ? event.metaKey : ((key === 91) ? true : false),
            dialog = null,
            Dialog = null;

        switch (true) {
          // Command + O
          case key === 79 && command:
            if ($('#designer-dialog').children().length) {
              $('#' + $("#designer-dialog").children()[0].id).removeClass('fade').modal('hide');
            }
            Dialog = runtime.require('DialogCommand');
            dialog = new Dialog({'title':''});
            dialog.show();
            event.stopPropagation();
            event.preventDefault();
            return false;
            break;
          default:
            break;
        }
      }.bind(this));
  	} else {
      document.body.addEventListener('keydown', function keydown(event){
        var key = event.which || event.keyCode;
        var ctrl = event.ctrlKey ? event.ctrlKey : ((key === 17) ? true : false);

        switch (true) {
          // Ctrl + O
          case key === 79 && ctrl:
            if ($('#designer-dialog').children().length) {
              $('#' + $("#designer-dialog").children()[0].id).removeClass('fade').modal('hide');
            }
            Dialog = runtime.require('DialogCommand');
            dialog = new Dialog({'title':''});
            dialog.show();
            event.stopPropagation();
            event.preventDefault();
            return false;
            break;
          default:
            break;
        }
      }.bind(this)); 
  	}
  }
}
