function start() { 
  var http = global.require('http'),
      logger = this.require('logger');

  http.createServer(function (req, res) {
    res.writeHead(200);
    res.end('hello world\n');
  }).listen(1234);

  logger.info('server is running at http://127.0.0.1:1234');
}
